query GetFeed($page: Int, $size: Int, $withFriends: Boolean!) {
  feed(withFriends: $withFriends) {
    photos(page: $page, size: $size) {
      edges {
        node {
          id
          src
          country {
            code
            name
            flag
            __typename
          }
          description
          likes {
            total
            likes {
              user
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      pageInfo {
        hasPreviousPage
        hasNextPage
        __typename
      }
      __typename
    }
    stat {
      count
      country {
        code
        __typename
      }
      __typename
    }
    __typename
  }
}
